import { useContext, useState } from 'react';
import './stories.scss';
import { AuthContext } from '../../context/authContext';
import { makeRequest } from '../../axios';
import { useQuery } from '@tanstack/react-query';

const Stories = () => {
  const { currentUser } = useContext(AuthContext);
  const [popupOpen, setPopupOpen] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);

  const { isLoading, error, data: users, refetch } = useQuery(['users'], () =>
    makeRequest.get('/users').then((res) => {
      return res.data;
    }),
  );

  const getRandomUsers = (users, count, excludedUserId) => {
    if (!users || users.length === 0 || count <= 0) {
      return [];
    }
  
    const filteredUsers = users.filter(user => user.id !== excludedUserId);
  
    const shuffledUsers = filteredUsers.sort(() => 0.5 - Math.random());
    return shuffledUsers.slice(0, count);
  };
  
  const randomUsers = getRandomUsers(users, 4, currentUser.id);

  console.log(randomUsers)

  const handleStoryClick = (image) => {
    setSelectedImage(image);
    setPopupOpen(true);
    setTimeout(() => {
      setPopupOpen(false);
    }, 2000); 
  };
  

  // TEMPORARY
  // const stories = [
  //   {
  //     id: 1,
  //     name: 'Đắc Lãm',
  //     img: '1684573163447301136306_187175800387803_844743252080188780_n.jpg',
  //   },
  //   {
  //     id: 2,
  //     name: 'Hoàng Long',
  //     img: '1684575953040312889028_3304383109784974_3562308693713513832_n.jpg',
  //   },
  //   {
  //     id: 3,
  //     name: 'Diệu Anh',
  //     img: '1684576106464340925706_178104478438778_5807967013547051182_n.jpg',
  //   },
  //   {
  //     id: 4,
  //     name: 'Trung Nhân',
  //     img: '1684938231461272766740_945847382985978_7965310120149237518_n.jpg',
  //   },
  //   {
  //     id: 5,
  //     name: 'Diệu Anh',
  //     img: '1684576106464340925706_178104478438778_5807967013547051182_n.jpg',
  //   },
  //   {
  //     id: 6,
  //     name: 'Trung Nhân',
  //     img: '1684938231461272766740_945847382985978_7965310120149237518_n.jpg',
  //   },
  // ];

  return (
    <div className="stories">
      <div className="story">
        <img src={'/upload/' + currentUser.profilePic} alt="" onClick={() => handleStoryClick(currentUser.profilePic)}/>
        <span>{currentUser.name}</span>
        <button>+</button>
      </div>
      {stories.map((story) => (
        <div className="story" key={story.id}>
          <img src={'/upload/' + story.profilePic} alt="" onClick={() => handleStoryClick(story.profilePic)}/>
          <span>{story.name}</span>
        </div>
      ))}
      {popupOpen && (
        <div className="popup">
          <img src={'/upload/' + selectedImage} alt="Story" />
        </div>
      )}
    </div>
  );
};

export default Stories;
