import axios from 'axios';
import { useContext, useEffect, useState } from 'react';
import './rightBar.scss';
import { AuthContext } from '../../context/authContext';
import { makeRequest } from '../../axios';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import { Link } from 'react-router-dom';
import { handleScroll } from '../../activate/scroll';

const RightBar = () => {
  const { currentUser } = useContext(AuthContext);
  const queryClient = useQueryClient();
  const userId = currentUser.id;
  const [userNotFollows, setUserNotFollow] = useState([]);
  const [userFollows, setUserFollow] = useState([]);

  useEffect(() => {
    makeRequest.get(`/users/unfollowers/` + userId)
      .then((res) => setUserNotFollow(res.data))
      .catch((err) => {
        throw new Error(err.response.data);
      });
  }, [userId]);

  useEffect(() => {
    makeRequest.get(`/users/followers/` + userId)
      .then((res) => setUserFollow(res.data))
      .catch((err) => {
        throw new Error(err.response.data);
      });
  }, [userId]);

  // UnFollowers
  const { isLoading: isLoadingNotFollow, error: errorNotFollow, data: userNotFollow } = useQuery(['unfollowers', userId], () => {
    return makeRequest.get(`/users/unfollowers/` + userId)
      .then((res) => res.data)
      .catch((err) => {
        throw new Error(err.response.data);
      });
  });
  
  console.log(userNotFollow);
  
  //Followers
  const { isLoading: isLoadingFollow, error: errorFollow, data: userFollow } = useQuery(['followers', userId], () => {
    return makeRequest.get(`/users/followers/` + userId)
      .then((res) => res.data)
      .catch((err) => {
        throw new Error(err.response.data);
      });
  });
  
  console.log(userFollow);
  
  console.log(userId);

  return (
    <div className="rightBar">
      <div className="container">
        <div className="item">
          <span>Suggestions For You</span>
          {errorNotFollow ? 'Something went wrong!' : isLoadingNotFollow ? '' : 
          userNotFollow.map((user) => (
            <Link
              to={'/profile/' + user.id}
              style={{ textDecoration: 'none', color: 'inherit' }}
              onClick={handleScroll}
            >
              <div className="user" key={user.id}>
                <div className="userInfo">
                  <img
                    src={'/upload/' + user.profilePic}
                    alt=""
                  />
                  <span>{user.name}</span>
                </div>
                <div className="buttons">
                  <button>follow</button>
                </div>
              </div>
            </Link>
          ))}
        </div>
        <div className="item">
          <span>List Friend</span>
          {errorFollow ? 'Something went wrong!' : isLoadingFollow ? '' :
            userFollow.map((user) => (
              <Link
              to={'/profile/' + user.id}
              style={{ textDecoration: 'none', color: 'inherit' }}
              onClick={handleScroll}
              >
                <div className='user' key={user.id}>
                  <div className='userInfo'>
                    <img src={'/upload/' + user.profilePic} alt="" />
                    <p>
                      <span>{user.name}</span>
                    </p>
                  </div>
                  <div className="status">
                    <span>offline</span>
                  </div>
                </div>
              </Link>
            ))
          }
        </div>
      </div>
    </div>
  );
};

export default RightBar;
