import axios from 'axios';
import { useContext, useEffect, useState } from 'react';
import './rightBar.scss';
import { AuthContext } from '../../context/authContext';
import { makeRequest } from '../../axios';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';

const RightBar = () => {
  const { currentUser } = useContext(AuthContext);
  const queryClient = useQueryClient();
  const userId = currentUser.id;

  const { isLoading, error, data:userNotFollow } = useQuery(['users', userId], () => {
    return makeRequest.get(`/users/unfollowers/` + userId)
      .then((res) => res.data)
      .catch((err) => {
        throw new Error(err.response.data);
      });
  });

  const { isLoad, err, data:userFollow } = useQuery(['users', userId], () => {
    return makeRequest.get(`/users/followers/` + userId)
      .then((res) => res.data)
