import axios from 'axios';
import { useContext, useEffect, useState } from 'react';
import './rightBar.scss';
import { AuthContext } from '../../context/authContext';
import { makeRequest } from '../../axios';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';

const RightBar = () => {
  const { currentUser } = useContext(AuthContext);
  const queryClient = useQueryClient();
  const userId = currentUser.id;

  const { isLoading, error, data: users } = useQuery(['users', userId], () =>
    makeRequest.get('/users/follows/' + userId).then((res) => {
      return res.data;
    }),
  );

  const getRandomUsers = (users, count, excludedUserId) => {
    if (!users || users.length === 0 || count <= 0) {
      return [];
    }
  
    const filteredUsers = users.filter(user => user.id !== excludedUserId);
  
    const shuffledUsers = filteredUsers.sort(() => 0.5 - Math.random());
    return shuffledUsers.slice(0, count);
  };
  
  const usersFollowed = getRandomUsers(users, 2, currentUser.id);

  console.log(usersFollowed);

  const usersNotFollowed = getRandomUsers(users, 2, currentUser.id);

  console.log(usersNotFollowed);

  // TEMPORARY
  const userFollowed = [
    {
      id: 1,
      name: 'Đắc Lãm',
      img: '1684573163447301136306_187175800387803_844743252080188780_n.jpg',
