import { useContext, useState } from 'react';
import './stories.scss';
import { AuthContext } from '../../context/authContext';
import { handleScroll } from '../../activate/scroll';

const Stories = () => {
  const { currentUser } = useContext(AuthContext);
  const [popupOpen, setPopupOpen] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);

  const handleStoryClick = (image) => {
    setSelectedImage(image);
    setPopupOpen(true);
    setTimeout(() => {
      setPopupOpen(false);
    }, 5000); 
  };
  

  //TEMPORARY
  const stories = [
    {
      id: 1,
      name: 'Đắc Lãm',
      img: '1684573163447301136306_187175800387803_844743252080188780_n.jpg',
    },
    {
      id: 2,
      name: 'Hoàng Long',
      img: '1684575953040312889028_3304383109784974_3562308693713513832_n.jpg',
    },
    {
      id: 3,
      name: 'Diệu Anh',
      img: '1684576106464340925706_178104478438778_5807967013547051182_n.jpg',
    },
    {
      id: 4,
      name: 'Trung Nhân',
      img: '1684938231461272766740_945847382985978_7965310120149237518_n.jpg',
    },
  ];

  return (
    <div className="stories">
      <div className="story">
        <img src={'/upload/' + currentUser.profilePic} alt="" onClick={() => handleStoryClick(currentUser.profilePic)}/>
        <span>{currentUser.name}</span>
        <button>+</button>
      </div>
      {stories.map((story) => (
        <div className="story" key={story.id}>
          <img src={'/upload/' + story.img} alt="" onClick={() => handleStoryClick(story.img)}/>
          <span>{story.name}</span>
        </div>
      ))}
      {popupOpen && (
        <div className="popup">
          <img src={'/upload/' + selectedImage} alt="Story" />
        </div>
      )}
    </div>
  );
};

export default Stories;
