import './navbar.scss';
import DarkModeOutlinedIcon from '@mui/icons-material/DarkModeOutlined';
import WbSunnyOutlinedIcon from '@mui/icons-material/WbSunnyOutlined';
import NotificationsOutlinedIcon from '@mui/icons-material/NotificationsOutlined';
import EmailOutlinedIcon from '@mui/icons-material/EmailOutlined';
import PersonOutlinedIcon from '@mui/icons-material/PersonOutlined';
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import { Link } from 'react-router-dom';
import { useContext, useState } from 'react';
import { DarkModeContext } from '../../context/darkModeContext';
import { AuthContext } from '../../context/authContext';
import { handleScroll } from '../../activate/scroll';

const Navbar = () => {
  const { toggle, darkMode } = useContext(DarkModeContext);
  const { currentUser, logout } = useContext(AuthContext);
  const [isVisible, setIsVisible] = useState(false);

  const handleClick = () => {
    setIsVisible(!isVisible);
  };

  return (
    <div className="navbar">
      <div className="left">
        <Link to="/" style={{ textDecoration: 'none' }} onClick={handleScroll}>
          <span>JK24 Social</span>
        </Link>
        {darkMode ? (
          <WbSunnyOutlinedIcon className="icon" onClick={toggle} />
        ) : (
          <DarkModeOutlinedIcon className="icon" onClick={toggle} />
        )}
      </div>
      <div className="search">
        <SearchOutlinedIcon />
        <input type="search" placeholder="Search..." />
      </div>
      <div className="right">
        <EmailOutlinedIcon className="icon" />
        <PersonOutlinedIcon className="icon" />
        <NotificationsOutlinedIcon className="icon" />
        <div className="user" onClick={handleClick}>
          {isVisible && (
            <div className="user-menu">
              <ul class="user-list">
                <li class="user-item">
                  <a href="">Cài đặt & hỗ trợ</a>
                </li>
                <li class="user-item">
                  <a href="">Đóng góp ý kiến</a>
                </li>
                <hr />
                <li class="user-item">
                  <a to="./login" onClick={logout}>
                    Đăng xuất
                  </a>
                </li>
              </ul>
            </div>
          )}

          <img src={'/upload/' + currentUser.profilePic} alt="" />
          <span>{currentUser.name}</span>
        </div>
      </div>
    </div>
  );
};

export default Navbar;
